css:
.print-heading {
	text-align: left;
	text-transform: uppercase;
	color: #666;
	padding-bottom: 10px;
	margin-bottom: 10px;
	border-bottom: 1px solid #d1d8dd;
}

.print-heading h2 {
	font-size: 24px;
}

.print-format th {
	background-color: #eee !important;
	border-bottom: 0px !important;
}

.print-format .primary.compact-item {
    font-weight: bold;
}

.print-format div{
    line-height:0.5;
}
.print-format br{
    padding: 0; border-spacing: 0!important;  margin:0!important; line-height:0.2;
}

.print-format .section-break{
    line-height: 0.3;
}

.print-format .row:not(.section-break) {
    line-height: 0.3;
    margin-top: 3px !important;
    margin-bottom: 3px !important;
}

.print-format .section-break:not(:last-child) {
    margin-bottom: 0 !important;
}

/* modern format: for-test */
